import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { Box, useColorModeValue, Flex } from "@chakra-ui/react";
import HeaderSection from "./HeaderSection";
import AvailableTeams from "./AvailableTeams";
import WheelSection from "./WheelSection";
import HistorySection from "./HistorySection";

function RandomPickerPage() {
  const location = useLocation();
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState(null);
  const [nextPresenter, setNextPresenter] = useState("");
  const [history, setHistory] = useState([]);

  const selectedProject = location.state?.project || {};
  const projectId = selectedProject.id;

  const bgColor = useColorModeValue("gray.50", "gray.800");
  const sectionBg = useColorModeValue("white", "gray.700");
  const sectionBorderColor = useColorModeValue("gray.300", "gray.600");

  // Fetch echipele și istoricul pentru proiect
  useEffect(() => {
    const fetchProjectState = async () => {
      try {
        const response = await fetch(`http://localhost:5000/projects/${projectId}/state`);
        const data = await response.json();

        if (data.last_presenter && data.next_presenter) {
          setRandomTeam(data.last_presenter);
          setNextPresenter(
            `Next time presenter: ${data.next_presenter} on ${new Date(
              data.next_presentation_date
            ).toLocaleDateString()}`
          );
        }
      } catch (error) {
        console.error("Failed to fetch project state:", error);
      }
    };

    const fetchTeams = async () => {
      try {
        const response = await fetch(`http://localhost:5000/projects/${projectId}/populate-teams`, {
          method: "POST",
        });
        const data = await response.json();

        if (data.teams && Array.isArray(data.teams)) {
          setTeams(data.teams);
        } else {
          console.error("Invalid data format:", data);
        }
      } catch (error) {
        console.error("Failed to fetch teams:", error);
      }
    };

    fetchProjectState();
    fetchTeams();
    fetchHistory(); // Inițializează istoricul la montarea paginii
  }, [projectId]);

  // Fetch istoricul pentru proiect
  const fetchHistory = async () => {
    try {
      const response = await fetch(`http://localhost:5000/projects/${projectId}/history`);
      const data = await response.json();

      if (data.history && Array.isArray(data.history)) {
        setHistory(data.history); // Setează istoricul în stare
      } else {
        console.error("Invalid history format:", data);
      }
    } catch (error) {
      console.error("Failed to fetch history:", error);
    }
  };

  const handleDeleteTeam = async (teamName) => {
    try {
      const response = await fetch(
        `http://localhost:5000/projects/${projectId}/teams/${teamName}`,
        { method: "DELETE" }
      );

      if (!response.ok) {
        const errorData = await response.json();
        console.error("Failed to delete team:", errorData.message);
        return;
      }

      // Actualizează echipele disponibile după ștergere
      setTeams((prevTeams) => prevTeams.filter((team) => team !== teamName));
    } catch (error) {
      console.error("Failed to delete team:", error);
    }
  };

  const handleTeamSelect = async (selectedTeam) => {
    setRandomTeam(selectedTeam);

    const remainingTeams = teams.filter((team) => team !== selectedTeam);
    setTeams(remainingTeams);

    // Dacă rămân doar două echipe
    if (remainingTeams.length === 1) {
      const nextDate = new Date();
      nextDate.setDate(nextDate.getDate() + 7);

      // Salvăm starea în backend
      try {
        await fetch(`http://localhost:5000/projects/${projectId}/finalize`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            lastPresenter: selectedTeam,
            nextPresenter: remainingTeams[0],
          }),
        });

        setNextPresenter(`Next time presenter: ${remainingTeams[0]} on ${nextDate.toLocaleDateString()}`);
      } catch (error) {
        console.error("Failed to finalize project state:", error);
      }
    }

    // Persist echipele rămase
    try {
      await fetch(`http://localhost:5000/projects/${projectId}/update-teams`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ teams: remainingTeams }),
      });
    } catch (error) {
      console.error("Failed to update teams in database:", error);
    }

    // Actualizează istoricul după selecție
    try {
      await fetch(`http://localhost:5000/projects/${projectId}/history`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ teamName: selectedTeam }),
      });
      fetchHistory(); // Reîmprospătează istoricul
    } catch (error) {
      console.error("Failed to save history entry:", error);
    }
  };

  const handleReset = async () => {
    setRandomTeam(null);
    setNextPresenter("");

    try {
      const response = await fetch(`http://localhost:5000/projects/${projectId}/reset-teams`, {
        method: "POST",
      });
      const data = await response.json();

      if (data.teams && Array.isArray(data.teams)) {
        setTeams(data.teams);
        fetchHistory(); // Reîmprospătează istoricul după reset
      } else {
        console.error("Invalid data format during reset:", data);
      }
    } catch (error) {
      console.error("Failed to reset teams:", error);
    }
  };

  return (
    <Box
      bg={bgColor}
      p={10}
      minHeight="100vh"
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
    >
      <HeaderSection projectName={selectedProject.name} />
      <Box
        display="grid"
        gridTemplateColumns="1fr 2fr 1fr"
        gap={8}
        w="90%"
        bg={sectionBg}
        p={6}
        borderRadius="lg"
        boxShadow="xl"
        border={`2px solid ${sectionBorderColor}`}
        transition="transform 0.2s ease, box-shadow 0.2s ease"
        _hover={{
          transform: "scale(1.02)",
          boxShadow: "2xl",
        }}
      >
        <AvailableTeams teams={teams} background={sectionBg} onDelete={handleDeleteTeam} />
        <WheelSection
          teams={teams}
          randomTeam={randomTeam}
          nextPresenter={nextPresenter}
          onTeamSelect={handleTeamSelect}
          onReset={handleReset}
        />
        <HistorySection history={history} />
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
