import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Box,
  Heading,
  VStack,
  Text,
  Card,
  CardBody,
  Button,
  useColorModeValue,
  Flex,
  Divider,
} from "@chakra-ui/react";
import { motion, AnimatePresence } from "framer-motion";

function RandomPickerPage() {
  const location = useLocation();
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState(null);
  const [nextPresenter, setNextPresenter] = useState("");

  // Proiectul selectat vine din state-ul rutei
  const selectedProject = location.state?.project.name || "Unknown Project";

  const cardBg = useColorModeValue("gray.100", "gray.700");
  const hoverBg = useColorModeValue("teal.50", "teal.800");
  const availableTeamsBg = useColorModeValue("blue.50", "blue.900");

  // Fetch teams from backend
  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const response = await fetch("http://localhost:5000/teams");
        const data = await response.json();
        setTeams(data.teams); // SeteazÄƒ echipele disponibile
      } catch (error) {
        console.error("Failed to fetch teams:", error);
      }
    };

    fetchTeams();
  }, []);

  const handleTeamSelect = () => {
    if (teams.length === 0) return;

    // Selectare echipÄƒ aleatorie
    const randomIndex = Math.floor(Math.random() * teams.length);
    const selected = teams[randomIndex];

    setRandomTeam(selected);

    // ActualizeazÄƒ echipele rÄƒmase
    const remainingTeams = teams.filter((_, index) => index !== randomIndex);
    setTeams(remainingTeams);

    // DeterminÄƒ urmÄƒtorul prezentator
    if (remainingTeams.length === 1) {
      const nextDate = new Date();
      nextDate.setDate(nextDate.getDate() + 7);
      setNextPresenter(
        `Next time presenter: ${remainingTeams[0].name} on ${nextDate.toLocaleDateString()}`
      );
    }
  };

  const handleReset = () => {
    setRandomTeam(null);
    setNextPresenter("");

    // ReÃ®ncarcÄƒ echipele
    fetch("http://localhost:5000/teams")
      .then((response) => response.json())
      .then((data) => {
        setTeams(data.teams);
      })
      .catch((error) => console.error("Failed to reset teams:", error));
  };

  return (
    <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
      <Flex justify="center" mb={8}>
        <Heading
          size="2xl"
          color={useColorModeValue("teal.600", "teal.300")}
          textShadow="2px 2px #D0E8F2"
          fontWeight="extrabold"
        >
          ðŸŽ¯ {selectedProject}
        </Heading>
      </Flex>

      <Box
        display="grid"
        gridTemplateColumns="1fr 2fr"
        gap={8}
        alignItems="start"
      >
        {/* SecÈ›iunea pentru echipe disponibile */}
        <Box
          border="2px"
          borderColor="blue.500"
          borderRadius="lg"
          p={6}
          bg={availableTeamsBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="blue.700" textAlign="center">
            Available Teams
          </Heading>
          <Divider mb={4} borderColor="blue.300" />
          <AnimatePresence>
            {teams.map((team, index) => (
              <motion.div
                key={team.id}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 20 }}
                transition={{
                  duration: 0.5,
                  ease: "easeInOut",
                  delay: index * 0.1,
                }}
              >
                <Card
                  bg={cardBg}
                  p={4}
                  mb={3}
                  borderRadius="lg"
                  cursor="pointer"
                  transition="transform 0.2s ease"
                  _hover={{
                    transform: "scale(1.05)",
                    bg: hoverBg,
                  }}
                  boxShadow="md"
                >
                  <CardBody textAlign="center">
                    <Text fontWeight="bold">{team.name}</Text>
                  </CardBody>
                </Card>
              </motion.div>
            ))}
          </AnimatePresence>
        </Box>

        {/* SecÈ›iunea principalÄƒ */}
        <VStack spacing={8} bg={useColorModeValue("white", "gray.700")} p={8} borderRadius="lg" boxShadow="lg">
          {randomTeam ? (
            <Text fontSize="2xl" fontWeight="bold" color="green.500">
              Selected Team: {randomTeam.name}
            </Text>
          ) : (
            <Text fontSize="lg" fontWeight="bold" color="gray.600">
              Click "Pick Random Team" to select a team.
            </Text>
          )}
          {nextPresenter && (
            <Text
              fontSize="lg"
              fontWeight="bold"
              color="orange.600"
              bg={useColorModeValue("orange.100", "orange.700")}
              p={2}
              borderRadius="md"
            >
              {nextPresenter}
            </Text>
          )}
          <Button colorScheme="teal" onClick={handleTeamSelect} isDisabled={teams.length === 0}>
            Pick Random Team
          </Button>
          <Button colorScheme="red" onClick={handleReset}>
            Reset Teams
          </Button>
        </VStack>
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
