import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Box,
  Heading,
  VStack,
  Text,
  Card,
  CardBody,
  Button,
  useColorModeValue,
  Flex,
  Divider,
} from "@chakra-ui/react";
import { motion, AnimatePresence } from "framer-motion";
import RouletteWheel from "./RouletteWheel";

function RandomPickerPage() {
  const location = useLocation();
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState(null);
  const [nextPresenter, setNextPresenter] = useState("");
  const [history, setHistory] = useState([]);

  // Proiectul selectat vine din state-ul rutei
  const selectedProject = location.state?.projectName || "Unknown Project";

  const cardBg = useColorModeValue("gray.100", "gray.700");
  const hoverBg = useColorModeValue("teal.50", "teal.800");
  const availableTeamsBg = useColorModeValue("blue.50", "blue.900");
  const historyBg = useColorModeValue("orange.50", "orange.900");

  // Fetch teams from backend
  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const response = await fetch("http://localhost:5000/teams");
        const data = await response.json();
        setTeams(data.activeTeams); // Echipele disponibile
        setHistory(data.selectedTeams); // Istoricul echipelor selectate
      } catch (error) {
        console.error("Failed to fetch teams:", error);
      }
    };

    fetchTeams();
  }, []);

  const formatDate = (date) => {
    return date.toLocaleDateString(undefined, {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const handleTeamSelect = async () => {
    try {
      const response = await fetch("http://localhost:5000/random", {
        method: "POST",
      });
      const data = await response.json();

      setRandomTeam(data.selectedTeam); // Echipa selectată
      setTeams(data.remainingTeams); // Echipele rămase
      setHistory(data.selectedTeams); // Actualizare istoric

      if (data.remainingTeams.length === 1) {
        const nextDate = new Date();
        nextDate.setDate(nextDate.getDate() + 7);
        setNextPresenter(
          `Next time presenter: ${data.remainingTeams[0].name} on ${formatDate(nextDate)}`
        );
      }
    } catch (error) {
      console.error("Failed to select a team:", error);
    }
  };

  const handleReset = async () => {
    try {
      const response = await fetch("http://localhost:5000/teams");
      const data = await response.json();
      setTeams(data.activeTeams); // Resetează echipele disponibile
      setHistory([]); // Resetează istoricul
      setRandomTeam(null); // Resetează echipa selectată
      setNextPresenter(""); // Resetează mesajul pentru prezentatorul următor
    } catch (error) {
      console.error("Failed to reset teams:", error);
    }
  };

  return (
    <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
      <Flex justify="center" mb={8}>
        <Heading
          size="2xl"
          color={useColorModeValue("teal.600", "teal.300")}
          textShadow="2px 2px #D0E8F2"
          fontWeight="extrabold"
        >
          🎯 {selectedProject}
        </Heading>
      </Flex>

      <Box
        display="grid"
        gridTemplateColumns="1fr 2fr 1fr"
        gap={8}
        alignItems="start"
      >
        {/* Secțiunea pentru echipe disponibile */}
        <Box
          border="2px"
          borderColor="blue.500"
          borderRadius="lg"
          p={6}
          bg={availableTeamsBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="blue.700" textAlign="center">
            Available Teams
          </Heading>
          <Divider mb={4} borderColor="blue.300" />
          <AnimatePresence>
            {teams.map((team, index) => (
              <motion.div
                key={team.id}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 20 }}
                transition={{
                  duration: 0.5,
                  ease: "easeInOut",
                  delay: index * 0.1,
                }}
              >
                <Card
                  bg={cardBg}
                  p={4}
                  mb={3}
                  borderRadius="lg"
                  cursor="pointer"
                  transition="transform 0.2s ease"
                  _hover={{
                    transform: "scale(1.05)",
                    bg: hoverBg,
                  }}
                  boxShadow="md"
                >
                  <CardBody textAlign="center">
                    <Text fontWeight="bold">{team.name}</Text>
                  </CardBody>
                </Card>
              </motion.div>
            ))}
          </AnimatePresence>
        </Box>

        {/* Secțiunea principală cu roata */}
        <VStack spacing={8} bg={useColorModeValue("white", "gray.700")} p={8} borderRadius="lg" boxShadow="lg">
          {teams.length > 1 ? (
            <RouletteWheel teams={teams.map((t) => t.name)} onSelect={handleTeamSelect} />
          ) : (
            randomTeam && (
              <Text fontSize="2xl" fontWeight="bold" color="green.500">
                No need for a wheel! Today presenter: {randomTeam.name}
              </Text>
            )
          )}
          {nextPresenter && (
            <Text
              fontSize="lg"
              fontWeight="bold"
              color="orange.600"
              bg={useColorModeValue("orange.100", "orange.700")}
              p={2}
              borderRadius="md"
            >
              {nextPresenter}
            </Text>
          )}
          <Button colorScheme="red" onClick={handleReset}>
            Reset Cycle
          </Button>
        </VStack>

        {/* Secțiunea pentru istoricul echipelor selectate */}
        <Box
          border="2px"
          borderColor="orange.500"
          borderRadius="lg"
          p={6}
          bg={historyBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="orange.700" textAlign="center">
            Selected Teams History
          </Heading>
          <Divider mb={4} borderColor="orange.300" />
          <AnimatePresence>
            {history.map((entry, index) => (
              <motion.div
                key={entry.team_id + index}
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 10 }}
                transition={{ duration: 0.3 }}
              >
                <Box
                  bg={useColorModeValue("orange.100", "orange.700")}
                  p={4}
                  mb={3}
                  borderRadius="lg"
                  boxShadow="md"
                >
                  <Text fontWeight="bold">{entry.name}</Text>
                  <Text fontSize="sm" color="gray.600">
                    {entry.date}
                  </Text>
                </Box>
              </motion.div>
            ))}
          </AnimatePresence>
        </Box>
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
