import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Box,
  Heading,
  VStack,
  Text,
  Button,
  useColorModeValue,
  Flex,
  Divider,
} from "@chakra-ui/react";
import RouletteWheel from "./RouletteWheel";

function RandomPickerPage() {
  const location = useLocation();
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState(null);
  const [nextPresenter, setNextPresenter] = useState("");

  // Proiectul selectat vine din state-ul rutei
  const selectedProject = location.state?.project || {};
  const projectId = selectedProject.id;

  const availableTeamsBg = useColorModeValue("blue.50", "blue.900");

  // Fetch teams for the project from backend
  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const response = await fetch(`http://localhost:5000/projects/${projectId}/teams`);
        const data = await response.json();
        setTeams(data.teams); // SeteazÄƒ echipele disponibile pentru proiect
      } catch (error) {
        console.error("Failed to fetch teams:", error);
      }
    };

    fetchTeams();
  }, [projectId]);

  const updateTeamsInDatabase = async (updatedTeams) => {
    try {
      await fetch(`http://localhost:5000/projects/${projectId}/teams`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ teams: updatedTeams }),
      });
    } catch (error) {
      console.error("Failed to update teams in database:", error);
    }
  };

  const handleTeamSelect = async (selectedTeam) => {
    setRandomTeam(selectedTeam);

    // EliminÄƒ echipa selectatÄƒ din lista disponibilÄƒ
    const remainingTeams = teams.filter((team) => team !== selectedTeam);
    setTeams(remainingTeams);

    // PersistÄƒ echipele rÄƒmase Ã®n baza de date
    await updateTeamsInDatabase(remainingTeams);

    // DacÄƒ rÄƒmÃ¢ne doar o echipÄƒ dupÄƒ selecÈ›ie
    if (remainingTeams.length === 1) {
      const nextDate = new Date();
      nextDate.setDate(nextDate.getDate() + 7);
      setNextPresenter(
        `Next time presenter: ${remainingTeams[0]} on ${nextDate.toLocaleDateString()}`
      );
    }
  };

  const handleReset = async () => {
    setRandomTeam(null);
    setNextPresenter("");

    // ReÃ®ncarcÄƒ echipele din backend È™i persistÄƒ lista completÄƒ
    try {
      const response = await fetch(`http://localhost:5000/projects/${projectId}/teams`);
      const data = await response.json();
      setTeams(data.teams);

      // PersistÄƒ echipele reÃ®ncÄƒrcate Ã®n baza de date
      await updateTeamsInDatabase(data.teams);
    } catch (error) {
      console.error("Failed to reset teams:", error);
    }
  };

  return (
    <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
      <Flex justify="center" mb={8}>
        <Heading
          size="2xl"
          color={useColorModeValue("teal.600", "teal.300")}
          textShadow="2px 2px #D0E8F2"
          fontWeight="extrabold"
        >
          ðŸŽ¯ {selectedProject.name}
        </Heading>
      </Flex>

      <Box display="grid" gridTemplateColumns="1fr 2fr" gap={8} alignItems="start">
        {/* SecÈ›iunea pentru echipe disponibile */}
        <Box
          border="2px"
          borderColor="blue.500"
          borderRadius="lg"
          p={6}
          bg={availableTeamsBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="blue.700" textAlign="center">
            Available Teams
          </Heading>
          <Divider mb={4} borderColor="blue.300" />
          <VStack spacing={3} align="start">
            {teams.map((team) => (
              <Text
                key={team}
                fontSize="lg"
                fontWeight="bold"
                color={useColorModeValue("blue.800", "blue.200")}
              >
                {team}
              </Text>
            ))}
          </VStack>
        </Box>

        {/* SecÈ›iunea pentru roatÄƒ È™i alte interacÈ›iuni */}
        <VStack
          spacing={8}
          bg={useColorModeValue("white", "gray.700")}
          p={8}
          borderRadius="lg"
          boxShadow="lg"
          align="center"
        >
          {teams.length > 1 ? (
            <RouletteWheel teams={teams} onSelect={handleTeamSelect} />
          ) : (
            randomTeam && (
              <>
                <Text fontSize="2xl" fontWeight="bold" color="green.500">
                  Today presenter: {randomTeam}
                </Text>
                <Text
                  fontSize="lg"
                  fontWeight="bold"
                  color="orange.600"
                  bg={useColorModeValue("orange.100", "orange.700")}
                  p={2}
                  borderRadius="md"
                >
                  {nextPresenter}
                </Text>
              </>
            )
          )}
          <Button colorScheme="red" onClick={handleReset}>
            Reset Teams
          </Button>
        </VStack>
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
