import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  VStack,
  Text,
  Card,
  CardBody,
  Button,
  useColorModeValue,
  Flex,
  Divider,
  Alert,
  AlertIcon,
  Select,
} from "@chakra-ui/react";

function RandomPickerPage() {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState("");
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState("");
  const [nextPresenter, setNextPresenter] = useState("");
  const [history, setHistory] = useState([]);
  const [error, setError] = useState("");

  // ListeazÄƒ proiectele hardcodate pentru testare
  const hardcodedProjects = [
    { id: 1, name: "Project Alpha" },
    { id: 2, name: "Project Beta" },
    { id: 3, name: "Project Gamma" },
  ];

  // Echipele pentru fiecare proiect
  const projectTeams = {
    "Project Alpha": ["Alpha Team 1", "Alpha Team 2", "Alpha Team 3"],
    "Project Beta": ["Beta Team 1", "Beta Team 2", "Beta Team 3"],
    "Project Gamma": ["Gamma Team 1", "Gamma Team 2", "Gamma Team 3"],
  };

  useEffect(() => {
    // SetÄƒm proiectele hardcodate
    setProjects(hardcodedProjects);
    // SelectÄƒm primul proiect automat
    if (hardcodedProjects.length > 0) {
      setSelectedProject(hardcodedProjects[0].name);
    }
  }, []);

  useEffect(() => {
    if (selectedProject && projectTeams[selectedProject]) {
      setTeams(projectTeams[selectedProject]);
      setRandomTeam("");
      setNextPresenter("");
      setHistory([]);
    } else {
      setError("Project not found.");
    }
  }, [selectedProject]);

  const cardBg = useColorModeValue("gray.100", "gray.700");
  const hoverBg = useColorModeValue("teal.50", "teal.800");
  const availableTeamsBg = useColorModeValue("blue.50", "blue.900");
  const historyBg = useColorModeValue("orange.50", "orange.900");

  const formatDate = (date) => {
    return date.toLocaleDateString(undefined, {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const handleTeamSelect = (team) => {
    setRandomTeam(team);

    if (teams.length === 2) {
      const remainingTeam = teams.find((t) => t !== team);
      const nextDate = new Date();
      nextDate.setDate(nextDate.getDate() + 7);
      setNextPresenter(`Next time presenter: ${remainingTeam} on ${formatDate(nextDate)}`);
    }

    setTeams((prev) => prev.filter((t) => t !== team));
    setHistory((prev) => [
      ...prev,
      { name: team, date: new Date().toLocaleString() },
    ]);
  };

  const handleReset = () => {
    setTeams(projectTeams[selectedProject] || []);
    setRandomTeam("");
    setNextPresenter("");
    setHistory([]);
  };

  const handleProjectChange = (e) => {
    setSelectedProject(e.target.value);
  };

  if (error) {
    return (
      <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
        <Alert status="error">
          <AlertIcon />
          {error}
        </Alert>
      </Box>
    );
  }

  return (
    <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
      <Flex justify="center" mb={8}>
        <Heading
          size="2xl"
          color={useColorModeValue("teal.600", "teal.300")}
          textShadow="2px 2px #D0E8F2"
          fontWeight="extrabold"
        >
          ðŸŽ¯ {selectedProject}
        </Heading>
      </Flex>

      {/* Select pentru proiecte */}
      <Box mb={8}>
        <Select
          placeholder="Select a project"
          value={selectedProject}
          onChange={handleProjectChange}
        >
          {projects.map(project => (
            <option key={project.id} value={project.name}>
              {project.name}
            </option>
          ))}
        </Select>
      </Box>

      <Box
        display="grid"
        gridTemplateColumns="1fr 2fr 1fr"
        gap={8}
        alignItems="start"
      >
        {/* SecÈ›iunea pentru echipe disponibile */}
        <Box
          border="2px"
          borderColor="blue.500"
          borderRadius="lg"
          p={6}
          bg={availableTeamsBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="blue.700" textAlign="center">
            Available Teams
          </Heading>
          <Divider mb={4} borderColor="blue.300" />
          {teams.length === 0 ? (
            <Text textAlign="center">No teams available.</Text>
          ) : (
            <VStack spacing={4}>
              {teams.map((team, index) => (
                <Card
                  key={index}
                  bg={cardBg}
                  p={4}
                  borderRadius="lg"
                  cursor="pointer"
                  transition="transform 0.2s ease"
                  _hover={{
                    transform: "scale(1.05)",
                    bg: hoverBg,
                  }}
                  boxShadow="md"
                  onClick={() => handleTeamSelect(team)}
                >
                  <CardBody textAlign="center">
                    <Text fontWeight="bold">{team}</Text>
                  </CardBody>
                </Card>
              ))}
            </VStack>
          )}
        </Box>

        {/* SecÈ›iunea principalÄƒ */}
        <VStack spacing={8} bg={useColorModeValue("white", "gray.700")} p={8} borderRadius="lg" boxShadow="lg">
          {randomTeam && (
            <Text fontSize="2xl" fontWeight="bold" color="green.500">
              Selected Team: {randomTeam}
            </Text>
          )}
          {nextPresenter && (
            <Text
              fontSize="lg"
              fontWeight="bold"
              color="orange.600"
              bg={useColorModeValue("orange.100", "orange.700")}
              p={2}
              borderRadius="md"
            >
              {nextPresenter}
            </Text>
          )}
          <Button colorScheme="red" onClick={handleReset}>
            Reset Cycle
          </Button>
        </VStack>

        {/* SecÈ›iunea pentru istoricul echipelor selectate */}
        <Box
          border="2px"
          borderColor="orange.500"
          borderRadius="lg"
          p={6}
          bg={historyBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="orange.700" textAlign="center">
            Selected Teams History
          </Heading>
          <Divider mb={4} borderColor="orange.300" />
          {history.length === 0 ? (
            <Text textAlign="center">No history yet.</Text>
          ) : (
            <VStack spacing={4}>
              {history.map((entry, index) => (
                <Box
                  key={index}
                  bg={useColorModeValue("orange.100", "orange.700")}
                  p={4}
                  borderRadius="lg"
                  boxShadow="md"
                >
                  <Text fontWeight="bold">{entry.name}</Text>
                  <Text fontSize="sm" color="gray.600">
                    {formatDate(new Date(entry.date))}
                  </Text>
                </Box>
              ))}
            </VStack>
          )}
        </Box>
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
