import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Box,
  Heading,
  VStack,
  Text,
  Card,
  CardBody,
  Button,
  useColorModeValue,
  Flex,
  Divider,
  Spinner,
  Alert,
  AlertIcon,
  Select, // ImportÄƒ componenta Select din Chakra UI
} from "@chakra-ui/react";
import { motion, AnimatePresence } from "framer-motion";
import RouletteWheel from "./RouletteWheel";
import axios from "axios";

function RandomPickerPage() {
  const location = useLocation();
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(location.state?.projectName || "");
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState("");
  const [nextPresenter, setNextPresenter] = useState("");
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  // FuncÈ›ie pentru a obÈ›ine proiectele
  const fetchProjects = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/projects`);
      setProjects(response.data.projects);
      // DacÄƒ nu este selectat niciun proiect, selecteazÄƒ primul proiect disponibil
      if (!selectedProject && response.data.projects.length > 0) {
        setSelectedProject(response.data.projects[0].name);
      }
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch projects.");
      setLoading(false);
    }
  };

  // FuncÈ›ie pentru a obÈ›ine echipele active È™i cele selectate
  const fetchTeams = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/teams`);
      const { activeTeams, selectedTeams } = response.data;

      setTeams(activeTeams.map(team => team.name));
      setHistory(selectedTeams.map(entry => ({
        name: entry.teams.name, // AsigurÄƒ-te cÄƒ backend-ul returneazÄƒ `teams.name`
        date: entry.selected_at, // AsigurÄƒ-te cÄƒ backend-ul returneazÄƒ `selected_at`
      })));
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch teams.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  useEffect(() => {
    if (selectedProject) {
      fetchTeams();
    }
  }, [selectedProject]);

  const cardBg = useColorModeValue("gray.100", "gray.700");
  const hoverBg = useColorModeValue("teal.50", "teal.800");
  const availableTeamsBg = useColorModeValue("blue.50", "blue.900");
  const historyBg = useColorModeValue("orange.50", "orange.900");

  const formatDate = (date) => {
    const d = new Date(date);
    return d.toLocaleDateString(undefined, {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const handleTeamSelect = async (team) => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.post(`${API_URL}/random`, { projectName: selectedProject });
      const { selectedTeam, remainingTeams } = response.data;
      setRandomTeam(selectedTeam.name);

      setTeams(remainingTeams.map(t => t.name));

      setHistory(prev => [
        ...prev,
        { name: selectedTeam.name, date: new Date().toLocaleString() },
      ]);

      if (remainingTeams.length === 1) {
        const nextTeam = remainingTeams[0].name;
        const nextDate = new Date();
        nextDate.setDate(nextDate.getDate() + 7);
        setNextPresenter(`Next time presenter: ${nextTeam} on ${formatDate(nextDate)}`);
      } else {
        setNextPresenter("");
      }

      setLoading(false);
    } catch (err) {
      setError("Failed to select a random team.");
      setLoading(false);
    }
  };

  const handleReset = async () => {
    setLoading(true);
    setError("");
    try {
      await axios.post(`${API_URL}/reset`, { projectName: selectedProject });
      await fetchTeams();
      setRandomTeam("");
      setNextPresenter("");
      setHistory([]);
      setLoading(false);
    } catch (err) {
      setError("Failed to reset cycle.");
      setLoading(false);
    }
  };

  // FuncÈ›ie pentru a gestiona schimbarea proiectului selectat
  const handleProjectChange = (e) => {
    setSelectedProject(e.target.value);
    setRandomTeam("");
    setNextPresenter("");
    setHistory([]);
  };

  if (loading) {
    return (
      <Flex justify="center" align="center" height="100vh">
        <Spinner size="xl" />
      </Flex>
    );
  }

  return (
    <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
      <Flex justify="center" mb={8}>
        <Heading
          size="2xl"
          color={useColorModeValue("teal.600", "teal.300")}
          textShadow="2px 2px #D0E8F2"
          fontWeight="extrabold"
        >
          ðŸŽ¯ {selectedProject || "Select a Project"}
        </Heading>
      </Flex>

      {error && (
        <Alert status="error" mb={4}>
          <AlertIcon />
          {error}
        </Alert>
      )}

      {/* Select pentru proiecte */}
      <Box mb={8}>
        <Select
          placeholder="Select a project"
          value={selectedProject}
          onChange={handleProjectChange}
        >
          {projects.map(project => (
            <option key={project.id} value={project.name}>
              {project.name}
            </option>
          ))}
        </Select>
      </Box>

      <Box
        display="grid"
        gridTemplateColumns="1fr 2fr 1fr"
        gap={8}
        alignItems="start"
      >
        {/* SecÈ›iunea pentru echipe disponibile */}
        <Box
          border="2px"
          borderColor="blue.500"
          borderRadius="lg"
          p={6}
          bg={availableTeamsBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="blue.700" textAlign="center">
            Available Teams
          </Heading>
          <Divider mb={4} borderColor="blue.300" />
          <AnimatePresence>
            {teams.map((team, index) => (
              <motion.div
                key={team}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 20 }}
                transition={{
                  duration: 0.5,
                  ease: "easeInOut",
                  delay: index * 0.1,
                }}
              >
                <Card
                  bg={cardBg}
                  p={4}
                  mb={3}
                  borderRadius="lg"
                  cursor="pointer"
                  transition="transform 0.2s ease"
                  _hover={{
                    transform: "scale(1.05)",
                    bg: hoverBg,
                  }}
                  boxShadow="md"
                  onClick={() => handleTeamSelect(team)}
                >
                  <CardBody textAlign="center">
                    <Text fontWeight="bold">{team}</Text>
                  </CardBody>
                </Card>
              </motion.div>
            ))}
          </AnimatePresence>
        </Box>

        {/* SecÈ›iunea principalÄƒ cu roata */}
        <VStack spacing={8} bg={useColorModeValue("white", "gray.700")} p={8} borderRadius="lg" boxShadow="lg">
          {teams.length > 1 ? (
            <RouletteWheel teams={teams} onSelect={handleTeamSelect} />
          ) : (
            randomTeam && (
              <Text fontSize="2xl" fontWeight="bold" color="green.500">
                No need for a wheel! Today presenter: {randomTeam}
              </Text>
            )
          )}
          {nextPresenter && (
            <Text
              fontSize="lg"
              fontWeight="bold"
              color="orange.600"
              bg={useColorModeValue("orange.100", "orange.700")}
              p={2}
              borderRadius="md"
            >
              {nextPresenter}
            </Text>
          )}
          <Button colorScheme="red" onClick={handleReset}>
            Reset Cycle
          </Button>
        </VStack>

        {/* SecÈ›iunea pentru istoricul echipelor selectate */}
        <Box
          border="2px"
          borderColor="orange.500"
          borderRadius="lg"
          p={6}
          bg={historyBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="orange.700" textAlign="center">
            Selected Teams History
          </Heading>
          <Divider mb={4} borderColor="orange.300" />
          <AnimatePresence>
            {history.map((entry, index) => (
              <motion.div
                key={entry.name + index}
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 10 }}
                transition={{ duration: 0.3 }}
              >
                <Box
                  bg={useColorModeValue("orange.100", "orange.700")}
                  p={4}
                  mb={3}
                  borderRadius="lg"
                  boxShadow="md"
                >
                  <Text fontWeight="bold">{entry.name}</Text>
                  <Text fontSize="sm" color="gray.600">
                    {formatDate(entry.date)}
                  </Text>
                </Box>
              </motion.div>
            ))}
          </AnimatePresence>
        </Box>
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
