import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Box,
  Heading,
  VStack,
  Text,
  Button,
  useColorModeValue,
  Flex,
  Divider,
} from "@chakra-ui/react";
import RouletteWheel from "./RouletteWheel";

function RandomPickerPage() {
  const location = useLocation();
  const [teams, setTeams] = useState([]);
  const [randomTeam, setRandomTeam] = useState(null);
  const [history, setHistory] = useState([]);

  // Proiectul selectat vine din state-ul rutei
  const selectedProject = location.state?.project.name || "Unknown Project";

  const availableTeamsBg = useColorModeValue("blue.50", "blue.900");
  const historyBg = useColorModeValue("orange.50", "orange.900");

  // Fetch teams from backend
  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const response = await fetch("http://localhost:5000/teams");
        const data = await response.json();
        setTeams(data.teams); // SeteazÄƒ echipele disponibile
      } catch (error) {
        console.error("Failed to fetch teams:", error);
      }
    };

    fetchTeams();
  }, []);

  const handleTeamSelect = (selectedTeam) => {
    setRandomTeam(selectedTeam);

    // ActualizeazÄƒ echipele rÄƒmase
    const remainingTeams = teams.filter((team) => team !== selectedTeam);
    setTeams(remainingTeams);

    // AdaugÄƒ echipa selectatÄƒ Ã®n istoric
    setHistory((prevHistory) => [
      ...prevHistory,
      { name: selectedTeam, date: new Date().toLocaleString() },
    ]);
  };

  const handleReset = () => {
    setRandomTeam(null);
    setHistory([]);

    // ReÃ®ncarcÄƒ echipele
    fetch("http://localhost:5000/teams")
      .then((response) => response.json())
      .then((data) => {
        setTeams(data.teams);
      })
      .catch((error) => console.error("Failed to reset teams:", error));
  };

  return (
    <Box bg={useColorModeValue("gray.50", "gray.800")} p={10} minHeight="100vh">
      <Flex justify="center" mb={8}>
        <Heading
          size="2xl"
          color={useColorModeValue("teal.600", "teal.300")}
          textShadow="2px 2px #D0E8F2"
          fontWeight="extrabold"
        >
          ðŸŽ¯ {selectedProject}
        </Heading>
      </Flex>

      <Box display="grid" gridTemplateColumns="1fr 2fr 1fr" gap={8} alignItems="start">
        {/* SecÈ›iunea pentru echipe disponibile */}
        <Box
          border="2px"
          borderColor="blue.500"
          borderRadius="lg"
          p={6}
          bg={availableTeamsBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="blue.700" textAlign="center">
            Available Teams
          </Heading>
          <Divider mb={4} borderColor="blue.300" />
          <VStack spacing={3} align="start">
            {teams.map((team) => (
              <Text
                key={team}
                fontSize="lg"
                fontWeight="bold"
                color={useColorModeValue("blue.800", "blue.200")}
              >
                {team}
              </Text>
            ))}
          </VStack>
        </Box>

        {/* SecÈ›iunea principalÄƒ cu roata */}
        <VStack spacing={8} bg={useColorModeValue("white", "gray.700")} p={8} borderRadius="lg" boxShadow="lg">
          <RouletteWheel teams={teams} onSelect={handleTeamSelect} />
          {randomTeam && (
            <Text fontSize="2xl" fontWeight="bold" color="green.500">
              Selected Team: {randomTeam}
            </Text>
          )}
          <Button colorScheme="red" onClick={handleReset}>
            Reset Teams
          </Button>
        </VStack>

        {/* SecÈ›iunea pentru istoricul selecÈ›iilor */}
        <Box
          border="2px"
          borderColor="orange.500"
          borderRadius="lg"
          p={6}
          bg={historyBg}
          boxShadow="lg"
          transition="transform 0.3s ease"
          _hover={{ transform: "scale(1.02)" }}
        >
          <Heading size="lg" mb={4} color="orange.700" textAlign="center">
            Selection History
          </Heading>
          <Divider mb={4} borderColor="orange.300" />
          <VStack spacing={3} align="start">
            {history.map((entry, index) => (
              <Box
                key={index}
                bg={useColorModeValue("orange.100", "orange.700")}
                p={4}
                borderRadius="lg"
                boxShadow="md"
              >
                <Text fontWeight="bold">{entry.name}</Text>
                <Text fontSize="sm" color={useColorModeValue("gray.600", "gray.200")}>
                  {entry.date}
                </Text>
              </Box>
            ))}
          </VStack>
        </Box>
      </Box>
    </Box>
  );
}

export default RandomPickerPage;
